package cn.edu.cqupt.mis.user.register.dao;import cn.edu.cqupt.mis.user.register.model.UserRegisterMessage;import org.springframework.stereotype.Repository;/** * @author:dengsiyuan * @Date:2018/9/29 9:06 */@Repository("userRegisterDao")public interface UserRegisterDao {    /**     * 插入新的用户信息     * @param userRegisterMessage     * @return int:-1插入失败     * */    int insertNewUser(UserRegisterMessage userRegisterMessage);    /**     * 判断用户学号是否已经存在     * @param userId     * @return userId     * */    UserRegisterMessage judgeUserId(int userId);    /**     * 判断用户邮箱是否已经被用     * @param eMail     * @return eMail     * */    String judgeUserEmail(String eMail);}