/*创建数据表*/DROP TABLE IF EXISTS `user_message`;CREATE TABLE `user_message` (  `user_name` varchar(10) NOT NULL COMMENT '用户名',  `user_id` int(10) NOT NULL COMMENT '用户学号',  `password` varchar(6) NOT NULL COMMENT '用户密码',  `e_mail` varchar(50) NOT NULL COMMENT '用户邮箱',  `sex` char(2) DEFAULT NULL COMMENT '用户性别',  `phone_number` bigint(11) DEFAULT NULL COMMENT '用户手机号',  `major` varchar(50) DEFAULT NULL COMMENT '用户专业',  `class_number` int(8) DEFAULT NULL COMMENT '用户班级',  `study_direction` varchar(10) DEFAULT NULL COMMENT '学习方向',  `age` int(3) DEFAULT NULL COMMENT '用户年龄',  `birth` date DEFAULT NULL COMMENT '用户年龄',  `state` int(1) NOT NULL DEFAULT '0' COMMENT '用户状态',  `label_info` varchar(50) DEFAULT '这人真懒！！什么都没留下......',  PRIMARY KEY (`user_id`),  UNIQUE KEY `e_mail_index` (`e_mail`),  UNIQUE KEY `phone_number_index` (`phone_number`),  KEY `password_index` (`password`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;