package cn.edu.cqupt.mis.user.register.dao;import cn.edu.cqupt.mis.user.UserApplication;import cn.edu.cqupt.mis.user.UserApplicationTests;import cn.edu.cqupt.mis.user.register.model.UserMailCode;import cn.edu.cqupt.mis.user.register.model.UserRegisterMessage;import org.junit.Assert;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.test.context.ContextConfiguration;import org.springframework.test.context.junit.jupiter.SpringJUnitConfig;import org.springframework.test.context.junit4.SpringRunner;import org.springframework.transaction.annotation.Transactional;/** * @author:dengsiyuan * @Date:2018/9/30 10:03 */@RunWith(SpringRunner.class)@ContextConfiguration(classes = UserApplication.class)public class UserRegisterTest {    @Autowired    private UserRegisterDao userRegisterDao;    /*     * test插入新的用户     **/    @Test    @Transactional    public void test_insertNewUser(){        int expected = 1;        UserRegisterMessage user = new UserRegisterMessage();        user.setUserId(2016211005);        user.setUserName("zq");        user.setEMail("789456123@qq.com");        user.setPassword("123456");        user.setStudyDirection("前端");        int result = userRegisterDao.insertNewUser(user);        Assert.assertEquals(expected,result);    }    /*    * 判断用户账号是否已经存在    * */    @Test    @Transactional    public void test_judgeUserId(){        UserRegisterMessage result = userRegisterDao.judgeUserId(2017211005);        Assert.assertNotNull(result);    }}